import React, { useState, useRef } from "react";
import WalletList from "./components/WalletList";
import TransactionForm from "./components/TransactionForm";
import TransactionList from "./components/TransactionList";
import AppBar from "@mui/material/AppBar";
import Toolbar from "@mui/material/Toolbar";
import Typography from "@mui/material/Typography";
import Box from "@mui/material/Box";
import { ThemeProvider, createTheme } from "@mui/material/styles";
import Button from "@mui/material/Button";

const theme = createTheme({
    palette: {
        primary: {
            main: "#d2e3eb", // Light blue
        },
        secondary: {
            main: "#1a1f36", // Dark blue
        },
    },
});

const App = () => {
    const [selectedWallet, setSelectedWallet] = useState(null);
    const transactionListRef = useRef(); // Reference to the TransactionList component
    const userId = "User1"; // Replace with dynamic user input if needed

    // Trigger the transaction list to refresh
    const refreshTransactions = () => {
        if (transactionListRef.current) {
            transactionListRef.current.fetchTransactions();
        }
    };

    return (
        <ThemeProvider theme={theme}>
            <AppBar position="static">
                <Toolbar>
                    <Typography variant="h6" component="div" color="inherit">
                        Threshold Wallet
                    </Typography>
                </Toolbar>
            </AppBar>
            <Box
                component="main"
                sx={{
                    padding: "16px",
                    backgroundColor: "#f5f5f5",
                    minHeight: "calc(100vh - 64px)", // Adjust for AppBar height
                }}
            >
                {!selectedWallet ? (
                    <WalletList userId={userId} onSelectWallet={setSelectedWallet} />
                ) : (
                    <div style={{ display: "flex" }}>
                        {/* Left side: Transactions */}
                        <div style={{ flex: 1, padding: "10px", borderRight: "1px solid #ccc" }}>
                            <TransactionList
                                ref={transactionListRef}
                                walletId={selectedWallet.wallet_id}
                            />
                        </div>

                        {/* Right side: New Transaction Form */}
                        <div style={{ flex: 1, padding: "10px" }}>
                            <TransactionForm
                                wallet={selectedWallet}
                                userId={userId}
                                refreshTransactions={refreshTransactions}
                            />
                        </div>
                    </div>
                )}
                {selectedWallet && (
                    <Button
                        variant="contained"
                        color="secondary"
                        onClick={() => setSelectedWallet(null)}
                        sx={{ marginTop: "16px" }}
                    >
                        Back to Wallet List
                    </Button>
                )}
            </Box>
            <Box
                component="footer"
                sx={{
                    backgroundColor: "rgba(26, 31, 54, 0.9)", // Dark translucent blue
                    color: "#d2e3eb", // Light blue for text
                    padding: "10px",
                    textAlign: "center",
                    fontSize: "16px",
                }}
            >
                <Typography variant="body2">
                    Â© 2025 Crypto Wallet. All rights reserved.
                </Typography>
            </Box>
        </ThemeProvider>
    );
};

export default App;
