import React, { useState, useEffect } from "react";
import axios from "axios";

const API_URL = "http://127.0.0.1:8000"; // move URL varialbe to common place. 

const TransactionList = ({ walletId }) => {
    const [transactions, setTransactions] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchTransactions = async () => {
            try {
                const response = await axios.get(`${API_URL}/transactions/${walletId}`);
                setTransactions(response.data);
            } catch (error) {
                console.error("Failed to fetch transactions:", error);
            } finally {
                setLoading(false);
            }
        };
        fetchTransactions();
    }, [walletId]);

    if (loading) return <p>Loading transactions...</p>;

    return (
        <div>
            <h2>Transactions for Wallet ID: {walletId}</h2>
            <ul>
                {transactions.map((tx) => (
                    <li key={tx.transaction_id}>
                        <strong>Description:</strong> {tx.description} <br />
                        <strong>Status:</strong> {tx.status}
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default TransactionList;
