import axios from "axios";

const API_URL = "https://bitcoin-threshold-wallet.onrender.com"; // render-url
const LOCAL_HOST_URL ="http://127.0.0.1:8000"

// Fetch wallets for a user
export const getWallets = async (userId) => {
    const response = await axios.get(`${LOCAL_HOST_URL}/wallets/wallets/${userId}`);
    return response.data;
};

// Create a new transaction
export const createTransaction = async (data) => {
    const response = await axios.post(`${LOCAL_HOST_URL}/transactions/transactions/request`, data);
    return response.data;
};
